mapper.update:
UPDATE xxl_conf_user SET
password = #{password},
token = #{token}, status = #{status}, real_name = #{realName}, role = #{role}, appnames = #{appnames} WHERE id = #{id}

mapper.select:
SELECT 
FROM xxl_conf_user AS t
WHERE t.username = #{username}

SELECT 
FROM xxl_conf_user AS t


		AND t.username like CONCAT(CONCAT('%', #{username}), '%')


		AND t.status = #{status}


ORDER BY username ASC
LIMIT #{offset}, #{pagesize}

SELECT count(1) FROM xxl_conf_user AS t
AND t.username like CONCAT(CONCAT('%', #{username}), '%')
AND t.status = #{status}
